var piu = require('./index'), teoria = require('teoria');

function test(notes) {
  notes = notes.map(teoria.note);

  console.time('infer strict');
  console.log(
    piu.infer(notes, false).map(piu.name)
  );
  console.timeEnd('infer strict');
}

test(['d', 'a']);
test(['g', 'c']);
test(['c', 'f']);
test(['f4', 'c4']);
test(['d4', 'g3']);
